function changeBackground() {
    const body = document.querySelector('html');
    const date = new Date();
    const hour = date.getHours(); 
    let newBackground;

    if (hour >= 20 || hour < 4) {
        // From 8:00 PM to 4:00 AM
        newBackground = "url('/themes/CTFD-crimson-theme/static/img/background4.jpg')";
        console.log('Night time');
    } else if (hour >= 4 && hour < 12) {
        // From 4:00 AM to 12:00 PM
        newBackground = "url('/themes/CTFD-crimson-theme/static/img/sun-rise-background.jpg')";
        console.log('Morning time');
    } else if (hour >= 12 && hour < 18) {
        // From 12:00 PM to 6:00 PM
        newBackground = "url('/themes/CTFD-crimson-theme/static/img/day_background.jpg')";
        console.log('Afternoon time');
    } else if (hour >= 18 && hour < 20) {
        // From 6:00 PM to 8:00 PM
        newBackground = "url('/themes/CTFD-crimson-theme/static/img/sun-dusk-background.jpg')";
        console.log('Evening time');
    }

   
    if (body.style.backgroundImage !== newBackground) {
        body.style.transition = "background-image 1s ease-in-out"; 
        body.style.backgroundImage = newBackground;
    }
}


changeBackground();


setInterval(() => {
    const date = new Date();
    const minutes = date.getMinutes();
    
  
    if (minutes === 0) {
        changeBackground();
    }
}, 60000);
